import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';

//for 통신(npm install axios 필요)
import axios from "axios";


function App() {

  const [message, setMessage] = useState("");

  /*
  //user/login
  useEffect(() => {
    axios.post('http://192.249.18.215:443/user/login',
    {
      id: "tjsan0122",
      pw: "1234",
    },
    {
    headers:{
      'Content-type': 'application/json',
      'Accept': 'application/json'
      }
    })
    .then((Response)=> {
      if(Response.data != null){
        // 로그인 성공
        setMessage("로그인 성공");
        console.log(Response.data);
      }
      else{
        //회원가입 실패 
        setMessage("로그인 실패");
        console.log(Response.data);
      }
    })
  },[]);
  */


  //user/signup
  useEffect(() => {
    axios.post('http://192.249.18.215:443/user/signup',
    {
      id: "tj2",
      pw: "1234",
      username: "오선무"
    },
    {
    headers:{
      'Content-type': 'application/json',
      'Accept': 'application/json'
      }
    })
    .then((Response)=> {
      if(Response.data){
        // 회원가입 성공
        setMessage("회원가입 성공");
        console.log(Response.data);
      }
      else{
        //회원가입 실패 
        setMessage("회원가입 실패");
        console.log(Response.data);
      }
    })
  },[]);

   /*
  //look/upload == 아직 해결 안됨
  useEffect(() => {
    axios.post('http://192.249.18.215:443/look/upload',
    {
      id : "tjsan0122",
      tpos : ["DATE", "CAMPUS"],
      gender : "MAN",
      clothes :
      [
        {
        category :"OUTER",
        subCategory :"HOOD",
        gender :"MAN",
        color : "RED"
        }
      ]
    },
    {
      'Content-type': 'application/json',
    })
    .then((Resposnse) => {
        setMessage("업로드 성공");
    })
  },[]);
  */


 //look/uploadImg


  const [content, setContent] = useState("");



  useEffect(() => {
    const formData = new FormData();
    setContent("img/image1.PNG".target.files[0]);
    formData.append("img", content);

  axios.post('http://192.249.18.215:443/look/uploadImg', formData,
  {
  headers:
    {
    'Content-Type':'multipart/form-data'
    }
  })
  .then((Response)=> {
    setMessage("이미지 업로드 성공");
    console.log(Response.data);
  })
},[]);


  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h1 className="App-title">{message}</h1>
      </header>
      <p className="App-intro">                
        To get started, edit <code>src/App.js</code> and save to reload.            
      </p>
    </div>
  );
}

export default App;
